mapper.select:
SELECT COUNT(DISTINCT a.id)
FROM t_article a
LEFT JOIN t_article_tag tat on a.id = tat.article_id

    is_dekete = #{condition.isDelete}

        and article_title like concat('%', #{condition.keyword}, '%')


        and `status` = #{condition.status}


        and article_type = #{condition.articleType}


        and category_id = #{condition.categoryId}


        and tat.tag_id = #{condition.tagId}

SELECT a.id, article_cover, article_title, article_type, category_name, is_top, is_recommend, a.is_delete, a.`status`, a.create_time, t.id AS tag_id, t.tag_name FROM ( SELECT id, article_cover, category_id, article_title, article_type, is_top, is_recommend, is_delete, `status`, create_time FROM t_article
is_delete = #{condition.isDelete}
and article_title like concat('%',#{condition.keyword},'%')
and status = #{condition.status}
and category_id = #{condition.categoryId}
and article_type = #{condition.articleType}
and id in ( SELECT article_id FROM t_article_tag WHERE tag_id = #{condition.tagId} )
ORDER BY is_top DESC, is_recommend DESC, id DESC LIMIT #{limit}, #{size} ) a LEFT JOIN t_category c ON a.category_id = c.id LEFT JOIN t_article_tag tat ON a.id = tat.article_id LEFT JOIN t_tag t ON t.id = tat.tag_id
