mapper.select:
SELECT type_id as id, COUNT(*) AS comment_count FROM t_comment WHERE comment_type = #{commentType} AND type_id IN
#{typeId}
GROUP BY type_id

SELECT count(c.id) from t_comment c LEFT JOIN t_user u ON c.from_uid = u.id
c.comment_type = #{condition.commentType}
and u.nickname like concat('%',#{condition.keyword},'%')
and c.is_check = #{condition.isCheck}

SELECT c.id, u.avatar, u.nickname AS from_nickname, r.nickname AS to_nickname, a.article_title, c.comment_content, c.comment_type, c.is_check, c.create_time FROM t_comment c LEFT JOIN t_article a ON c.type_id = a.id LEFT JOIN t_user u ON c.from_uid = u.id LEFT JOIN t_user r ON c.to_uid = r.id
c.comment_type=#{condition.commentType}
AND u.nickname LIKE concat('%',#{condition.keyword},'%')
AND c.is_check = #{condition.isCheck}
ORDER BY c.id DESC LIMIT #{limit},#{size}
